import React from "react";
import "../assets/navbar.css";
import { Link, useLocation } from "react-router-dom";

export default function Header({ activeButton, setActiveButton }) {
  const location = useLocation();

  function handleButtonClick(buttonName) {
    setActiveButton(buttonName);
  }

  return (
    <nav className="navbar">
      <Link
        to="/"
        style={{ display: "block", height: "100%" }}
        onClick={() => handleButtonClick("Home")}
      >
        <button
          className={`abutton ${activeButton === "Home" ? "active" : ""}`}
        >
          Home
        </button>
      </Link>

      <Link
        to="/discover"
        style={{ display: "block", height: "100%" }}
        onClick={() => handleButtonClick("Discover")}
      >
        <button
          className={`abutton ${
            activeButton === "Discover" ? "active" : ""
          }`}
        >
          Discover
        </button>
      </Link>

      <Link
        to="/profile"
        style={{ display: "block", height: "100%" }}
        onClick={() => handleButtonClick("Profile")}
      >
        <button
          className={`abutton ${
            activeButton === "Profile" ? "active" : ""
          }`}
        >
          Profile
        </button>
      </Link>

      <button id="createpost" className="abutton">Create</button>
      <button id="login" className="abutton">Login</button>
    </nav>
  );
}
