import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../assets/login.css";
import LoginUser from "../components/utils/users/LoginUser.js"

export default function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [formValid, setFormValid] = useState(false);

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
    if (e.target.value && password) {
      setFormValid(true);
    } else {
      setFormValid(false);
    }
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
    if (e.target.value && username) {
      setFormValid(true);
    } else {
      setFormValid(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = await LoginUser(username, password);
    console.log(data);
    if (data) {
      const cookies = document.cookie.split(";"); // Split the cookies string into individual cookies
      let userCookie;
      for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.startsWith("user=")) {
          userCookie = cookie.substring("user=".length);
          break;
        }
      }
      if (userCookie) {
        const user = JSON.parse(userCookie); // Parse the user object from the cookie value
        console.log(user.id);
        navigate("/");
      }
    }
  };



  return (
    <>
      <div className="loginBox">
        <h1 className="title">Social Media</h1>
        <input
          type="text"
          className="input"
          placeholder="Username"
          value={username}
          onChange={handleUsernameChange}
        ></input>
        <input
          type="text"
          className="input"
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange}
        ></input>
        <button className="button" disabled={!formValid} onClick={handleSubmit}>
          Login
        </button>
      </div>
    </>
  );
}
