import React, { useState } from "react";
import "../assets/navbar.css";
import { Link, useLocation } from "react-router-dom";
import CreatePostBox from "./CreatePostBox";

export default function Navbar() {
  const [activeButton, setActiveButton] = useState("");
  const [showCreatePost, setShowCreatePost] = useState(false); // State for showing/hiding the CreatePostBox

  const location = useLocation();

  React.useEffect(() => {
    switch (location.pathname) {
      case "/":
        setActiveButton("Home");
        break;
      case "/discover":
        setActiveButton("Discover");
        break;
      case "/profile":
        setActiveButton("Profile");
        break;
      default:
        setActiveButton("");
    }
  }, [location]);

  const handleCreatePostClick = () => {
    setShowCreatePost(true);
  };

  const handleCloseCreatePost = () => {
    setShowCreatePost(false);
  };

  const handleCreatePost = (title, content) => {
    // Handle the creation of the post (e.g., make an API call)
    console.log("Creating post:", title, content);
  };

  return (
    <nav className="navbar">
      <Link
        to="/"
        style={{ display: "block", height: "100%" }}
        onClick={() => setActiveButton("Home")}
      >
        <button
          className={`abutton ${activeButton === "Home" ? "active" : ""}`}
        >
          Home
        </button>
      </Link>

      <Link
        to="/discover"
        style={{ display: "block", height: "100%" }}
        onClick={() => setActiveButton("Discover")}
      >
        <button
          className={`abutton ${
            activeButton === "Discover" ? "active" : ""
          }`}
        >
          Discover
        </button>
      </Link>

      <Link
        to="/profile"
        style={{ display: "block", height: "100%" }}
        onClick={() => setActiveButton("Profile")}
      >
        <button
          className={`abutton ${activeButton === "Profile" ? "active" : ""}`}
        >
          Profile
        </button>
      </Link>

      <button id="createpost" className="abutton" onClick={handleCreatePostClick}>
        Create Post
      </button>
      <button id="login" className="abutton">
        Login
      </button>

      {showCreatePost && (
        <CreatePostBox onClose={handleCloseCreatePost} onCreate={handleCreatePost} />
      )}
    </nav>
  );
}
