import React from "react";
import "../assets/post.css";
import GetAllPosts from "./utils/posts/GetAllPosts";
import GetUserPosts from "./utils/posts/GetUserPosts";
import DeletePost from "./utils/posts/DeletePost";
import GetOneUser from "./utils/users/GetOneUser";
import FollowUser from "./utils/users/FollowUser";
import UnfollowUser from "./utils/users/UnfollowUser";
import FollowedPosts from "./utils/posts/FollowedPosts";

function cookies() {
  const string = decodeURIComponent(document.cookie);
  const match = string.match(/\d+/);
  if (match !== null) {
    const number = parseInt(match[0], 10);
    if (!isNaN(number)) {
      return number;
    }
  }
  return null;
}

export default function Posts({ type }) {
  const [posts, setPosts] = React.useState([]);
  const [selectedPost, setSelectedPost] = React.useState(null);
  const [username, setUsername] = React.useState("");
  const [isFollowing, setIsFollowing] = React.useState(false); // Track follow status

  async function getUsername(UserId) {
    const user = await GetOneUser(UserId);
    const username = user.username;
    setUsername(username);
    return null;
  }

  React.useEffect(() => {
    async function fetchData() {
      let response;
      if (type === "Discover") {
        response = await GetAllPosts();
      } else if (type === "Home") {
        const userId = cookies(); // Retrieve the user ID from cookies
        response = await FollowedPosts(userId);
      } else {
        response = await GetUserPosts(cookies());
      }
      setPosts(response);

      // Check if the user is following the creator for the selected post
      if (selectedPost) {
        const isFollowingCreator = await checkFollowStatus(selectedPost.UserId);
        setIsFollowing(isFollowingCreator);
      }
    }
    fetchData();
  }, [type]);

  async function checkFollowStatus(UserId) {
    const userId = cookies();
    const isFollowing = await isUserFollowing(UserId, userId);
    return isFollowing;
  }

  async function handleFollowClick(event, UserId) {
    event.stopPropagation();
    const userId = cookies();
    if (isFollowing) {
      // User is already following, perform unfollow action
      await UnfollowUser(UserId, userId);
      setIsFollowing(false);
    } else {
      // User is not following, perform follow action
      await FollowUser(UserId, userId);
      setIsFollowing(true);
    }
  }


  if (!posts || posts.length === 0) {
    // Render an alternative UI or display a message when no posts are found
    return null;
  }

  return (
    <div>
      {selectedPost && (
        <div className={`post selected`}>
          <h2 className="title">{selectedPost.title}</h2>
          {(type === "Discover" || type === "Home") && <p className="creator">Creator: {selectedPost.UserId}</p>}
          <p className="content">{selectedPost.content}</p>
          <p className="date">Date: {selectedPost.postdate}</p>
          {(type === "Discover" || type === "Home") && (
            <div className="actions">
              <button className="like" onClick={handleLikeClick}>
                Like
              </button>
              <button className="follow" onClick={(event) => handleFollowClick(event, selectedPost.UserId)}>
                Follow
              </button>
            </div>
          )}
          {type !== "Discover" && type !== "Home" && (
            <button
              className="delete"
              onClick={(event) => handleDeleteClick(event, selectedPost.id)}
            >
              Delete
            </button>
          )}
          <button className="close" onClick={handleCloseClick}>
            Close
          </button>
        </div>
      )}
      <div className="post-container">
        {posts.map((post) => {
          if (post === selectedPost) {
            return null;
          }
          return (
            <div
              key={post.id}
              className={`post ${selectedPost === post ? "selected" : ""}`}
              onClick={() => handlePostClick(post)}
            >
              <h2 className="title">{post.title}</h2>
              {(type === "Discover" || type === "Home") && <p className="creator">Creator: {post.UserId}</p>}
              <p className="content">{post.content}</p>
              <p className="date">{post.postdate}</p>
              {(type === "Discover" || type === "Home") && (
                <div className="actions">
                  <button className="like" onClick={handleLikeClick}>
                    Like
                  </button>
                  <button className="follow" onClick={(event) => handleFollowClick(event, post.UserId)}>
                    Follow
                  </button>
                </div>
              )}
              {type !== "Discover" && type !== "Home" && (
              <button
                className="delete"
                onClick={(event) => handleDeleteClick(event, post.id)}
              >
                Delete
              </button>
)}

            </div>
          );
        })}
      </div>
      <div className={`overlay ${selectedPost ? "show" : ""}`} />
    </div>
  );

}
