import React, { useState } from "react";
import "../assets/createpost.css";

export default function CreatePostBox({ onClose, onCreate }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleContentChange = (e) => {
    setContent(e.target.value);
  };

  const handleSubmit = () => {
    // Check if both title and content are filled
    if (title && content) {
      // Handle create post logic here
      onCreate(title, content);
      // Clear the textboxes
      setTitle("");
      setContent("");
      // Close the CreatePostBox
      onClose();
    }
  };

  const isButtonDisabled = !(title && content); // Disable the button if either title or content is empty

  return (
    <div className="create-post-box">
      <h2>Create Post</h2>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={handleTitleChange}
      />
      <textarea
        placeholder="Content"
        value={content}
        onChange={handleContentChange}
      ></textarea>
      <button className="close-button" onClick={onClose}>
        Close
      </button>
      <button className="create-button" onClick={handleSubmit} disabled={isButtonDisabled}>
        Create
      </button>
    </div>
  );
}
