import React from "react";

import "../assets/header.css";

export default function Header() {
  const header = document.querySelector('.App-header');
const defaultHeaderHeight = getComputedStyle(header).getPropertyValue('--header-height');
const headerHeight = header.getBoundingClientRect().height;

document.documentElement.style.setProperty('--header-height', headerHeight + 'px');

window.addEventListener('scroll', () => {
  const scrollPos = window.scrollY;
  const newHeight = Math.max(headerHeight - scrollPos, 0);
  header.style.setProperty('height', newHeight + 'px');
});

window.addEventListener('resize', () => {
  const headerHeight = header.getBoundingClientRect().height;
  document.documentElement.style.setProperty('--header-height', headerHeight + 'px');
});


    return (
        <header className="App-header">
        <h1 className="App-title">A Social Media App</h1>
      </header>
    );
}
