import React, { useState } from "react";
import "../assets/navbar.css";
import { Link, useLocation, useNavigate } from "react-router-dom";

function cookies() {
  const string = decodeURIComponent(document.cookie);
  const number = parseInt(string.match(/\d+/)[0], 10);
  return number;
}

export default function Header() {
  const [activeButton, setActiveButton] = useState("");
  const location = useLocation();
  const navigate = useNavigate();

  React.useEffect(() => {
    switch (location.pathname) {
      case "/":
        setActiveButton("Home");
        break;
      case "/discover":
        setActiveButton("Discover");
        break;
      case "/profile":
        setActiveButton("Profile");
        break;
      default:
        setActiveButton("");
    }
  }, [location]);

  const handleLogout = () => {
    // Perform logout actions if necessary
    navigate("/login");
  };

  return (
    <nav className="navbar">
      <Link
        to="/"
        style={{ display: "block", height: "100%" }}
        onClick={() => cookies()}
      >
        <button
          className={`abutton ${activeButton === "Home" ? "active" : ""}`}
        >
          Home
        </button>
      </Link>

      <Link
        to="/discover"
        style={{ display: "block", height: "100%" }}
        onClick={() => setActiveButton("Discover")}
      >
        <button
          className={`abutton ${
            activeButton === "Discover" ? "active" : ""
          }`}
        >
          Discover
        </button>
      </Link>

      <Link
        to="/profile"
        style={{ display: "block", height: "100%" }}
        onClick={() => setActiveButton("Profile")}
      >
        <button
          className={`abutton ${activeButton === "Profile" ? "active" : ""}`}
        >
          Profile
        </button>
      </Link>

      <button id="createpost" className="abutton">
        Create Post
      </button>
      <button id="login" className="abutton" onClick={handleLogout}>
        Log Out
      </button>
    </nav>
  );
}
