import React, { useState } from "react";
import "../assets/navbar.css";
import { Link } from "react-router-dom";

export default function Header() {
  const [activeButton, setActiveButton] = useState("Home");

  const handleRefresh = () => {
    window.location.reload();
  };

  return (
    <nav className="navbar">
      <Link
        to="/"
        style={{ display: "block", height: "100%" }}
        onClick={() => {
          setActiveButton("Home");
          handleRefresh();
        }}
      >
        <button className={`abutton ${activeButton === "Home" ? "active" : ""}`}>
          Home
        </button>
      </Link>

      <Link
        to="/discover"
        style={{ display: "block", height: "100%" }}
        onClick={() => {
          setActiveButton("Discover");
          handleRefresh();
        }}
      >
        <button
          className={`abutton ${activeButton === "Discover" ? "active" : ""}`}
        >
          Discover
        </button>
      </Link>

      <Link
        to="/profile"
        style={{ display: "block", height: "100%" }}
        onClick={() => {
          setActiveButton("Profile");
          handleRefresh();
        }}
      >
        <button
          className={`abutton ${activeButton === "Profile" ? "active" : ""}`}
        >
          Profile
        </button>
      </Link>

      <button id="createpost" className="abutton" onClick={handleRefresh}>
        Create Post
      </button>
      <button id="login" className="abutton" onClick={handleRefresh}>
        Login
      </button>
    </nav>
  );
}
