export default function Posts() {
    const [posts, setPosts] = React.useState([]);
    const [selectedPost, setSelectedPost] = React.useState(null);

    React.useEffect(() => {
      async function fetchData() {
        const response = await GetAllPosts();
        setPosts(response);
      }

      fetchData();
    }, []);

    function handleClick(post) {
      setSelectedPost(post);
    }

    function handleReset() {
      setSelectedPost(null);
    }

    return (
      <div className="posts-container">
        {posts.map((post) => (
          <div
            key={post.id}
            className={`post${post === selectedPost ? ' post--selected' : ''}`}
            onClick={() => handleClick(post)}
          >
            <h2 className="title">{post.title}</h2>
            <p className="content">{post.content}</p>
          </div>
        ))}
        {selectedPost && (
          <div className="post--selected-overlay" onClick={handleReset}>
            <div className="post--selected-content">
              <h2 className="title">{selectedPost.title}</h2>
              <p className="content">{selectedPost.content}</p>
            </div>
          </div>
        )}
      </div>
    );
  }
