import React from "react";
import "../assets/post.css";
import GetAllPosts from "./utils/posts/GetAllPosts";
import GetUserPosts from "./utils/posts/GetUserPosts";

function cookies() {
  const string = decodeURIComponent(document.cookie);
  const match = string.match(/\d+/);
  if (match !== null) {
    const number = parseInt(match[0], 10);
    if (!isNaN(number)) {
      return number;
    }
  }
  return null;
}

export default function Posts({ Area }) {
  const [posts, setPosts] = React.useState([]);
  const [selectedPost, setSelectedPost] = React.useState(null);

  React.useEffect(() => {
    async function fetchData() {
      if (Area === "Discover") {
        const response = await GetAllPosts();
        setPosts(response);
      } else {
        const response = await GetUserPosts(cookies());
        setPosts(response ? [response] : []); // Ensure posts is an array
      }
    }
    fetchData();
  }, [Area]);

  function handlePostClick(post) {
    document.body.classList.add("lock-scrolling");
    setSelectedPost(post);
  }

  function handleCloseClick() {
    document.body.classList.remove("lock-scrolling");
    setSelectedPost(null);
  }


if (!posts || posts.length === 0) {
  // Render an alternative UI or display a message when no posts are found
  return 
}

  return (
    <div>
      {selectedPost && (
        <div className={`post selected`}>
          <h2 className="title">{selectedPost.title}</h2>
          <p className="content">{selectedPost.content}</p>
          <button className="close" onClick={handleCloseClick}>
            Close
          </button>
        </div>
      )}
      <div className="post-container">
        {posts.length > 0 ? (
          posts.map((post) => {
            if (post === selectedPost) {
              return null;
            }
            return (
              <div
                key={post.id}
                className="post"
                onClick={() => handlePostClick(post)}
              >
                <h2 className="title">{post.title}</h2>
                <p className="content">{post.content}</p>
              </div>
            );
          })
        ) : (
          <p>No posts found.</p>
        )}
      </div>
      <div className={`overlay ${selectedPost ? "show" : ""}`} />
      {selectedPost && <div className="button-overlay" />}
    </div>
  );
}
